mapper.select:
SELECT
-- 级联表的字段 ,shangjia.shangjia_name as shangjiaName ,shangjia.shangjia_phone as shangjiaPhone ,shangjia.shangjia_email as shangjiaEmail ,shangjia.shangjia_photo as shangjiaPhoto ,shangjia.shangjia_xingji_types as shangjiaXingjiTypes ,shangjia.new_money as newMoney ,shangjia.shangjia_content as shangjiaContent ,shangjia.shangjia_delete as shangjiaDelete
FROM shangjiajianyi a left JOIN shangjia shangjia ON a.shangjia_id = shangjia.id
and a.id in
#{item}
and ( a.shangjia_id = #{params.shangjiaId} )
and a.shangjiajianyi_name like CONCAT('%',#{params.shangjiajianyiName},'%')
and a.shangjiajianyi_content like CONCAT('%',#{params.shangjiajianyiContent},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.shangjia_id IS NOT NULL
and shangjia.shangjia_name like CONCAT('%',#{params.shangjiaName},'%')
and shangjia.shangjia_phone like CONCAT('%',#{params.shangjiaPhone},'%')
and shangjia.shangjia_email like CONCAT('%',#{params.shangjiaEmail},'%')
and shangjia.shangjia_xingji_types = #{params.shangjiaXingjiTypes}
and shangjia.new_money >= #{params.newMoneyStart}
and shangjia.new_money <= #{params.newMoneyEnd}
and shangjia.shangjia_content like CONCAT('%',#{params.shangjiaContent},'%')
and shangjia.shangjia_delete >= #{params.shangjiaDeleteStart}
and shangjia.shangjia_delete <= #{params.shangjiaDeleteEnd}
and shangjia.shangjia_delete = #{params.shangjiaDelete}
order by a.${params.orderBy} desc
